--
-- Cấu trúc bảng cho bảng `category`
--

CREATE TABLE `category` (
  `category_id` int(11) NOT NULL,
  `name` varchar(100) COLLATE utf8_vietnamese_ci NOT NULL,
  `description` text COLLATE utf8_vietnamese_ci,
  `image` varchar(255) COLLATE utf8_vietnamese_ci NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_vietnamese_ci;

--
-- Đang đổ dữ liệu cho bảng `category`
--

INSERT INTO `category` (`category_id`, `name`, `description`, `image`) VALUES
(3, 'GOOD TO BE BACK', '<p>Trở th&agrave;nh t&acirc;m điểm của mọi &aacute;nh nh&igrave;n với \"Good to be back\", bộ sưu tập đầu ti&ecirc;n của năm mới 2018 đến từ Luxury Life. Luxury Life tin rằng, một bữa tiệc sẽ kh&ocirc;ng thể n&agrave;o trọn vẹn nếu thiếu đi mất vẻ đẹp sang trọng v&agrave; quyến rũ của c&aacute;c qu&yacute; c&ocirc; trong trang phục dự tiệc bắt mắt.</p>', 'img/lookbook/Good_to_be_back.jpg'),
(4, 'MISS SUNSHINE', 'Chẳng phải tự nhiên mà màu đỏ luôn được yêu thích nhất trong dịp năm mới đến, và lần này, Luxury Life cũng quyết định lựa chọn sắc đỏ rực rỡ làm chủ đạo, bên cạnh trắng, đen và xám quen thuộc. Hi vọng sẽ mang đến thêm may mắn, sang trọng và vẻ ngoài tươi tắn dành cho bạn.', 'img/lookbook/Miss_Sunshine.jpg'),
(5, 'GOOD TIME TAN LINE', 'Với slip dress khoét sâu cách điệu, jumpsuits hiện đại và năng động hay sơ mi lụa sang trọng đến từ \"Good time tan lines\", chắc chắn rằng, dù lựa chọn thế nào, bạn sẽ luôn cảm thấy tự tin nhất, nổi bật nhất tại bất kì nơi đâu bạn xuất hiện.', 'img/lookbook/Good_time_tan_ines.jpg'),
(6, 'SUMMER CATCH', 'Luxury Life cùng với sự xuất hiện của rất nhiều item dự tiệc đa dạng đến từ bộ sưu tập \"Summer Catch\" sẽ là gợi ý hoàn hảo cho bạn, những cô gái còn đang còn loay hoay chưa tìm được cho mình một trang phục ưng ý.', 'img/lookbook/Summer_Catch.jpg'),
(7, 'SWEET HEART', 'Kẻ sọc tartan, biểu tượng của những cuộc nổi loạn và sự tinh tế. Bạn đang tìm một họa tiết in thú vị, phong cách và không bao giờ lỗi thời? Câu trả lời đơn giản chính là: kẻ sọc tartan.', 'img/lookbook/Sweet_Heart.jpg'),
(8, 'ESCAPE TO THE SEA', '\"Escape to the sea\" - mùa của những chuyến đi về biển. Chỉ cần nhớ đến hè là gắn liền với hình ảnh biển vàng nắng trải dài bãi cát trắng, chút nắng, chút gió, chút mằn mặn trong hơi thở của biển.', 'img/lookbook/Escape_To_The_Sea.jpg');

